import React, { useRef } from "react";
import emailjs from '@emailjs/browser';
import styled from 'styled-components';


export default function Contact(){
    const form = useRef();

        const sendEmail = (e) => {
            e.preventDefault();

            emailjs.sendForm('service_hnvbrdy', 'template_9pc4hce', form.current, 'J5FqVyJEonk-vdDF1')
                .then((result) => {
                    console.log(result.text);
                }, (error) => {
                    console.log(error.text);
                });
        };

        return (
            <section id="about">
                <div className="col-md-12">
                    <h1 style={{ color: "black", paddingTop: "5%", paddingBottom: "5%" }}>
                        <span>Contact</span>
                    </h1>
                </div>
                <StyledContactForm>
                    <div className="frame">
                        <div className="camera"></div>
                        <div className="inner-frame">
                            <div className="screen-header">
                            <div className="screen-header-left">
                                <div className="screen-header-left-mobile">9:16</div>
                            <div className="screen-header-button btnclose"></div>
                            <div className="screen-header-button btnmaximize"></div>
                            <div className="screen-header-button btnminimize"></div>
                            </div>
                                <div className="screen-header-right">
                                    <div className="screen-header-right-mobile"></div>
                                    <div className="screen-header-ellipsis"></div>
                                    <div className="screen-header-ellipsis"></div>
                                    <div className="screen-header-ellipsis"></div>
                                </div>
                            </div>
                            <form ref={form} onSubmit={sendEmail}>
                                <label>Name</label>
                                <input type="text" name="user_name" />
                                <label>Email</label>
                                <input type="email" name="user_email" />
                                <label>Message</label>
                                <textarea name="message" />
                                <input type="submit" value="Send" />
                            </form>
                            <div className="button">
                                <div className="inner-button">
                                </div>
                            </div>
                        </div>
                    </div>
                </StyledContactForm>
                    </section>


        );
}
