import React, { useRef } from "react";
import emailjs from '@emailjs/browser';
import styled from 'styled-components';


export default function Contact(){
    const form = useRef();

        const sendEmail = (e) => {
            e.preventDefault();

            emailjs.sendForm('service_hnvbrdy', 'template_9pc4hce', form.current, 'J5FqVyJEonk-vdDF1')
                .then((result) => {
                    console.log(result.text);
                }, (error) => {
                    console.log(error.text);
                });
        };

        return (
            <StyledContactForm>
                <div className="col-md-12">
                    <h1 style={{ color: "black" }}>
                        <span>Contact</span>
                    </h1>
                </div>
                <div className="frame">
                    <div className="camera"></div>
                    <div className="inner-frame">
                        <div className="button">
                            <div className="inner-button">
                            </div>
                        </div>
                    </div>
                </div>
            <form ref={form} onSubmit={sendEmail}>
                <label>Name</label>
                <input type="text" name="user_name" />
                <label>Email</label>
                <input type="email" name="user_email" />
                <label>Message</label>
                <textarea name="message" />
                <input type="submit" value="Send" />
            </form>
            </StyledContactForm>
        );
}
// Styles
const StyledContactForm = styled.div`
    width: 100%;
    background: white;

    .frame {
  width: 400px;
  height: 550px;
  border-radius: 15px;
  background: grey;
  position: relative;
  left: 450px;
  z-index: 1;
}

.camera {
  width: 10px;
  height: 10px;
  background: #000000;
  border-radius: 50px;
  position: relative;
  left: 200px;
  top: 10px;
  z-index: 2;
}

.inner-frame {
  width: 350px;
  height: 425px;
  background: #000000;
  position: absolute;
  left: 25px;
  top: 30px;
  z-index: 3;

}

.button {
  width: 60px;
  height: 60px;
  background: #DAE2DF;
  border-radius: 50px;
  position: absolute;
  top: 440px;
  left: 150px;
  z-index: 4;
}

.inner-button {
  width: 30px;
  height: 30px;
  background: #FFFFFF;
  border-radius: 15px;
  position: absolute;
  left: 15px;
  top: 15px;
  z-index: 5;
}
    `;
